}

  InfoRestaurant.fromJson(dynamic json) {
    if (json["businesses"] != null) {
      _businesses = [];
      json["businesses"].forEach((v) {
        _businesses.add(Businesses.fromJson(v));
      });
    }
    _total = json["total"];
  }

